mapper.select:
select

from  hx_product
where 1=1

    and pro_title like concat('%',#{proTitle,jdbcType=VARCHAR},'%')


    and pro_name like concat('%',#{proName,jdbcType=VARCHAR},'%')


    and pro_type = #{proType}


    and pro_use_type = #{proUseType}


    limit #{offset}, #{limit}
