mapper.select:
select
`menu_id`,`parent_id`,`name`,`url`,`perms`,`type`,`icon`,`order_num`,`gmt_create`,`gmt_modified`
from sys_menu

	 and menu_id = #{menuId} 
	 and parent_id = #{parentId} 
	 and name = #{name} 
	 and url = #{url} 
	 and perms = #{perms} 
	 and type = #{type} 
	 and icon = #{icon} 
	 and order_num = #{orderNum} 
	 and gmt_create = #{gmtCreate} 
	 and gmt_modified = #{gmtModified} 



		order by ${sort} ${order}


		order by menu_id desc



	limit #{offset}, #{limit}

select count(*) from sys_menu

	 and menu_id = #{menuId} 
	 and parent_id = #{parentId} 
	 and name = #{name} 
	 and url = #{url} 
	 and perms = #{perms} 
	 and type = #{type} 
	 and icon = #{icon} 
	 and order_num = #{orderNum} 
	 and gmt_create = #{gmtCreate} 
	 and gmt_modified = #{gmtModified}
